macroData.command:
// Garou: Unstoppable Momentum — reminder helper
// Posts a summary of your Unstoppable Momentum benefits while raging.

function getActor() {
  return (
    canvas?.tokens?.controlled?.[0]?.actor ??
    this?.actor ??
    this?.item?.actor ??
    game.user.character ??
    null
  );
}

const actor = getActor();
if (!actor) {
  ui.notifications.error("No Garou actor found for Unstoppable Momentum.");
  return;
}

ChatMessage.create({
  content: `<p><strong>${actor.name}</strong> is empowered by <b>Unstoppable Momentum</b> while raging:</p>
  <ul>
    <li><b>Iron Instincts:</b> Advantage on saving throws against effects that would paralyze, stun, or restrain you.</li>
    <li><b>Relentless Advance:</b> Ignore nonmagical difficult terrain; magical difficult terrain only costs extra movement if created by a creature with CR ≥ your Garou level (DM's call).</li>
    <li><b>Predatory Rush:</b> During your Rage, treat your normal movement as if it were your Dash speed for how far you can move this turn.</li>
  </ul>
  <p><i>These benefits end immediately when your Rage ends.</i></p>`,
  speaker: ChatMessage.getSpeaker({ actor })
});
