macroData.command:
// Garou: Relentless Momentum use tracker (once per turn)

function getTargetActor() {
  return (
    canvas?.tokens?.controlled?.[0]?.actor ??
    this?.actor ??
    this?.item?.actor ??
    game.user.character ??
    null
  );
}

function getTurnKey() {
  if (game.combat) return `${game.combat.round}.${game.combat.turn}`;
  return `no-combat.${Math.floor(game.time.worldTime)}`;
}

const actor = getTargetActor();
if (!actor) {
  ui.notifications.error("No actor found for Relentless Momentum.");
  return;
}

const item = this.item;
const flagKey = "relentlessMomentumTurn";
const scope = "garou";
const turnKey = getTurnKey();
const lastUsed = await actor.getFlag(scope, flagKey);

if (lastUsed === turnKey) {
  ui.notifications.warn("Relentless Momentum has already been used this turn.");
  return;
}

await actor.setFlag(scope, flagKey, turnKey);

ChatMessage.create({
  content: `<p><strong>${actor.name}</strong> triggers <b>Relentless Momentum</b> this turn. You may immediately make one extra melee weapon attack against another hostile within half your speed.</p>`,
  speaker: ChatMessage.getSpeaker({ actor })
});
